INSERT INTO `musician` VALUES (66, '1846年', '阿尔伯特·洛尔青', '维也纳', 48.20835, 16.37250, '1846年，阿尔伯特·洛尔青到维也纳任职。', '阿尔伯特·洛尔青');
INSERT INTO `musician` VALUES (67, '1848年', '阿尔伯特·洛尔青', '柏林', 52.51704, 13.38886, '但由于1848年革命导致剧院关闭，阿尔伯特·洛尔青失业回柏林。', '阿尔伯特·洛尔青');
