INSERT INTO `musician` VALUES (76, '1780年', '路易吉·凯鲁比尼', '博洛尼亚', 44.49367, 11.34303, '1780年，路易吉·凯鲁比尼更获得托斯卡尼大公的资助，前往博洛尼亚和米兰进修。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (77, '1780年', '路易吉·凯鲁比尼', '米兰', 45.46680, 9.19050, '1780年，路易吉·凯鲁比尼更获得托斯卡尼大公的资助，前往博洛尼亚和米兰进修。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (78, '1783年11月', '路易吉·凯鲁比尼', '威尼斯', 45.43719, 12.33459, '而路易吉·凯鲁比尼唯一的喜剧作品，《三妻之夫和无妻之夫》在1783年11月的威尼斯首演。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (79, '1785年', '路易吉·凯鲁比尼', '意大利', 42.63843, 12.67430, '深感受意大利的传统局限，并且锐意尝试，路易吉·凯鲁比尼在1785年前往伦敦，为皇家剧院制作了两套正歌剧和喜歌剧。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (80, '1785年', '路易吉·凯鲁比尼', '伦敦', 51.50732, -0.12765, '深感受意大利的传统局限，并且锐意尝试，路易吉·凯鲁比尼在1785年前往伦敦，为皇家剧院制作了两套正歌剧和喜歌剧。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (81, '1785年', '路易吉·凯鲁比尼', '巴黎', 48.85661, 2.35150, '1785年，路易吉·凯鲁比尼偏离原订行程，和朋友吉安巴提斯塔·维奥堤前往巴黎旅游，并被维奥提引见法国的玛丽·安托瓦内特王后和巴黎的上流社会。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (82, '1788年', '路易吉·凯鲁比尼', '巴黎', 48.85661, 2.35150, '1788年，路易吉·凯鲁比尼《得摩丰》在巴黎大歌剧院上演获得好评。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (83, '1791年', '路易吉·凯鲁比尼', '瑞士', 46.79856, 8.23197, '1791年，路易吉·凯鲁比尼的《艾莉莎》，以瑞士阿尔卑斯山麓为背景。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (84, '1805年-1806年', '路易吉·凯鲁比尼', '维也纳', 48.20835, 16.37250, '而最终路易吉·凯鲁比尼也在1805年-1806年获得任命为拿破仑在维也纳的音乐总监，令他的部分作品得以在维也纳上演。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (85, '1813年', '路易吉·凯鲁比尼', '西班牙', 39.32623, -4.83806, '随后1813年首演的《阿本塞拉赫家族的故事》，讲述西班牙摩尔人的格拉纳达王国末日的英雄歌剧，是路易吉·凯鲁比尼意欲与加斯帕罗·斯蓬蒂尼的《贞洁的修女》抗衡之作。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (86, '1822年', '路易吉·凯鲁比尼', '巴黎', 48.85661, 2.35150, '1822年，路易吉·凯鲁比尼被任命为巴黎国立高等音乐舞蹈学院院长，并完成路易吉·凯鲁比尼的教科书《对位法和赋格教程》。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (87, '1841年', '路易吉·凯鲁比尼', '法国', 46.60335, 1.88833, '1841年，路易吉·凯鲁比尼更成为法国历史上首位获得荣誉军团勋章司令勋位的音乐家。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (88, '1842年3月15日', '路易吉·凯鲁比尼', '巴黎', 48.85661, 2.35150, '1842年3月15日，路易吉·凯鲁比尼在巴黎逝世。', '路易吉·凯鲁比尼');
INSERT INTO `musician` VALUES (89, '1870年10月8日', '路易·维尔纳', '普瓦捷', 46.58026, 0.34020, '1870年10月8日，路易·维尔纳在普瓦捷出生。', '路易·维尔纳');
