INSERT INTO `musician` VALUES (91, '1772年11月18日', '路易·斐迪南亲王', 'Lichtenberg', 52.53216, 13.51189, '1772年11月18日，路易·斐迪南亲王在Lichtenberg出生。', '路易·斐迪南亲王');
INSERT INTO `musician` VALUES (92, '1793年', '路易·斐迪南亲王', '美因茨', 50.00123, 8.27625, '路易·斐迪南亲王于1793年在美因茨围城战中负伤。', '路易·斐迪南亲王');
INSERT INTO `musician` VALUES (93, '1806年10月10日', '路易·斐迪南亲王', '美因茨', 50.00123, 8.27625, '1806年10月10日，路易·斐迪南亲王率军在美因茨萨尔费尔德战役中迎战让·拉纳元帅的法国军事。', '路易·斐迪南亲王');
INSERT INTO `musician` VALUES (94, '1806年10月10日', '路易·斐迪南亲王', '法国', 46.60335, 1.88833, '1806年10月10日，路易·斐迪南亲王率军在美因茨萨尔费尔德战役中迎战让·拉纳元帅的法国军事。', '路易·斐迪南亲王');
