INSERT INTO `musician` VALUES (96, '1778年', '路德维希·范·贝多芬', '科隆', 50.93836, 6.95997, '路德维希·范·贝多芬在1778年时就能在科隆登台演出。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (97, '1787年', '路德维希·范·贝多芬', '维也纳', 48.20835, 16.37250, '1787年，路德维希·范·贝多芬决定带着科隆大主教马克西米利安·弗朗茨的推荐信前往维也纳拜访沃尔夫冈·阿马德乌斯·莫扎特。', '路德维希·范·贝多芬 沃尔夫冈·阿马德乌斯·莫扎特');
INSERT INTO `musician` VALUES (98, '1787年', '沃尔夫冈·阿马德乌斯·莫扎特', '维也纳', 48.20835, 16.37250, '1787年，路德维希·范·贝多芬决定带着科隆大主教马克西米利安·弗朗茨的推荐信前往维也纳拜访沃尔夫冈·阿马德乌斯·莫扎特。', '路德维希·范·贝多芬 沃尔夫冈·阿马德乌斯·莫扎特');
INSERT INTO `musician` VALUES (99, '1787年', '路德维希·范·贝多芬', '法国', 46.60335, 1.88833, '1787年起，路德维希·范·贝多芬受到教授欧洛吉奥·施内德的影响，使路德维希·范·贝多芬了解当时启蒙时代和法国大革命的思想。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (100, '1790年', '约瑟夫·海顿', '波恩', 50.73585, 7.10066, '1790年，奥地利作曲家约瑟夫·海顿在从维也纳到伦敦的旅程中两次在波恩逗留，接触到路德维希·范·贝多芬，并对前一首清唱剧大加赞赏，海顿因此劝科隆大主教将路德维希·范·贝多芬送到维也纳深造。', '约瑟夫·海顿 路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (101, '1790年', '路德维希·范·贝多芬', '波恩', 50.73585, 7.10066, '1790年，奥地利作曲家约瑟夫·海顿在从维也纳到伦敦的旅程中两次在波恩逗留，接触到路德维希·范·贝多芬，并对前一首清唱剧大加赞赏，海顿因此劝科隆大主教将路德维希·范·贝多芬送到维也纳深造。', '约瑟夫·海顿 路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (102, '1790年2月20日', '路德维希·范·贝多芬', '波恩', 50.73585, 7.10066, '1790年2月20日约瑟夫2世逝世，波恩读书与休养协会准备在3月份举行悼念，路德维希·范·贝多芬在施内德的建议之下创作了一部《为约瑟夫2世逝世所作的悼念清唱剧》，这也是路德维希·范·贝多芬第一部与政治沾上边的作品。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (103, '1792年', '路德维希·范·贝多芬', '维也纳', 48.20835, 16.37250, '于1792年，路德维希·范·贝多芬再度踏上前往维也纳的行程。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (104, '1795年', '路德维希·范·贝多芬', '维也纳', 48.20835, 16.37250, '1795年，路德维希·范·贝多芬在维也纳举行了第一次音乐会，，由他本人演奏钢琴。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (105, '1796年', '路德维希·范·贝多芬', '柏林', 52.51704, 13.38886, '1796年，路德维希·范·贝多芬的事业起升，路德维希·范·贝多芬先后在布拉格和布达佩斯演出，途经纽伦堡、莱比锡和柏林等地，并创作了《大提琴奏鸣曲》Op.5和《钢琴奏鸣曲》Op.2。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (106, '1796年', '路德维希·范·贝多芬', '莱比锡', 51.34063, 12.37473, '1796年，路德维希·范·贝多芬的事业起升，路德维希·范·贝多芬先后在布拉格和布达佩斯演出，途经纽伦堡、莱比锡和柏林等地，并创作了《大提琴奏鸣曲》Op.5和《钢琴奏鸣曲》Op.2。', '路德维希·范·贝多芬');
