INSERT INTO `musician` VALUES (110, '1796年', '路德维希·范·贝多芬', '布达佩斯', 47.49838, 19.04047, '1796年，路德维希·范·贝多芬的事业起升，路德维希·范·贝多芬先后在布拉格和布达佩斯演出，途经纽伦堡、莱比锡和柏林等地，并创作了《大提琴奏鸣曲》Op.5和《钢琴奏鸣曲》Op.2。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (111, '1802年', '路德维希·范·贝多芬', '维也纳', 48.20835, 16.37250, '1802年，路德维希·范·贝多芬到维也纳的修养地圣城，意图靠当地矿泉水浴，以治疗自己日益严重的耳硬化症。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (112, '1802年', '路德维希·范·贝多芬', '维也纳', 48.20835, 16.37250, '1802年，路德维希·范·贝多芬受到经营维也纳两个大歌剧院的布劳温男爵的委托，去创作一部歌剧。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (113, '1803年', '路德维希·范·贝多芬', '维也纳', 48.20835, 16.37250, '《第2号交响曲》很快在1803年，路德维希·范·贝多芬就在维也纳第一次与听众见面。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (114, '1803年', '路德维希·范·贝多芬', '维也纳', 48.20835, 16.37250, '1803年，路德维希·范·贝多芬也得到了维也纳戏剧院作曲家这一职位，并着手进行下一部交响曲的创作。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (115, '1805年', '路德维希·范·贝多芬', '维也纳', 48.20835, 16.37250, '1805年，路德维希·范·贝多芬法军开进维也纳，歌剧首演，但是可惜演出非常失败，第三天就被迫停演。', '路德维希·范·贝多芬');
INSERT INTO `musician` VALUES (116, '1812年', '路德维希·范·贝多芬', '维也纳', 48.20835, 16.37250, '1812年夏路德维希·范·贝多芬出外修养，在6月底离开维也纳，经在布拉格短暂逗留后7月5日去到Teplice，并会见了约翰·沃尔夫冈·冯·歌德。', '路德维希·范·贝多芬');
