INSERT INTO `musician` VALUES (692, '1842年', '夏尔·德·贝里奥', '巴黎', 48.85661, 2.35150, '1842年，贝洛特过身，巴黎国立高等音乐舞蹈学院有意找夏尔·德·贝里奥接任但被夏尔·德·贝里奥所拒绝。', '夏尔·德·贝里奥');
INSERT INTO `musician` VALUES (693, '1843年', '夏尔·德·贝里奥', '布鲁塞尔', 50.84367, 4.36744, '1843年，夏尔·德·贝里奥接受了布鲁塞尔皇家音乐学院的委任，成为小提琴教授，并在此成创立了“法比小提琴学派”，培训出亨利·维厄当、海因里希·威尔海姆·恩斯特、雷欧纳等著名小提琴家。', '夏尔·德·贝里奥 亨利·维厄当 海因里希·威尔海姆·恩斯特');
INSERT INTO `musician` VALUES (694, '1843年', '亨利·维厄当', '布鲁塞尔', 50.84367, 4.36744, '1843年，夏尔·德·贝里奥接受了布鲁塞尔皇家音乐学院的委任，成为小提琴教授，并在此成创立了“法比小提琴学派”，培训出亨利·维厄当、海因里希·威尔海姆·恩斯特、雷欧纳等著名小提琴家。', '夏尔·德·贝里奥 亨利·维厄当 海因里希·威尔海姆·恩斯特');
