INSERT INTO `musician` VALUES (719, '1871年', '古斯塔夫·马勒', '布拉格', 50.08747, 14.42125, '为了提高其成绩，古斯塔夫·马勒于1871年被送到布拉格的一所新学校中，但并不快乐，不久后又回到Jihlava。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (720, '1871年', '古斯塔夫·马勒', 'Jihlava', 49.39606, 15.59031, '为了提高其成绩，古斯塔夫·马勒于1871年被送到布拉格的一所新学校中，但并不快乐，不久后又回到Jihlava。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (721, '1881年', '古斯塔夫·马勒', '卢布尔雅那', 46.04981, 14.50678, '1881年古斯塔夫·马勒来到了卢布尔雅那，那里的剧院虽小但资源丰富，管理者也对拓展演出内容雄心勃勃。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (722, '1883年1月', '古斯塔夫·马勒', 'Olomouc', 49.59406, 17.25114, '1883年1月，古斯塔夫·马勒当上了Olomouc一家剧院的指挥。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (723, '1883年8月', '古斯塔夫·马勒', '卡塞尔', 51.30902, 9.47630, '1883年8月，古斯塔夫·马勒成为了卡塞尔市皇家剧院的音乐总监，不过这一头衔掩盖了古斯塔夫·马勒实际的下属地位。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (724, '1884年1月', '古斯塔夫·马勒', '卡塞尔', 51.30902, 9.47630, '1884年1月，当时名声斐然的指挥家汉斯·冯·彪罗来到了卡塞尔，古斯塔夫·马勒想争取到彪罗“永久助理”的职位未果。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (725, '1885年7月', '古斯塔夫·马勒', '布拉格', 50.08747, 14.42125, '古斯塔夫·马勒不愿意在卡塞尔停留，于是在1885年7月辞职，并在布拉格的布拉格国家歌剧院获得助理指挥的职位。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (726, '1886年', '古斯塔夫·马勒', '莱比锡', 51.34063, 12.37473, '1886年，古斯塔夫·马勒与驰名的莱比锡新剧院签订了6年的合同。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (727, '1886年', '古斯塔夫·马勒', '莱比锡', 51.34063, 12.37473, '1886年，古斯塔夫·马勒与驰名的莱比锡新剧院签订了6年的合同。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (728, '1886年', '古斯塔夫·马勒', '莱比锡', 51.34063, 12.37473, '1886年古斯塔夫·马勒离开布拉格，来到莱比锡新国家歌剧院，在此与年长的同事尼基施·阿图尔发生了争执，矛盾主要关于二人在新推出的《尼伯龙根的指环》四联剧中如何分工。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (729, '1886年', '古斯塔夫·马勒', '莱比锡', 51.34063, 12.37473, '1886年古斯塔夫·马勒离开布拉格，来到莱比锡新国家歌剧院，在此与年长的同事尼基施·阿图尔发生了争执，矛盾主要关于二人在新推出的《尼伯龙根的指环》四联剧中如何分工。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (730, '1888年', '古斯塔夫·马勒', '布达佩斯', 47.49838, 19.04047, '1888年古斯塔夫·马勒来到布达佩斯时，发现匈牙利的民族主义者与德奥文化传统的拥护者之间发生着冲突。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (731, '1888年5月', '古斯塔夫·马勒', '莱比锡', 51.34063, 12.37473, '1888年5月，在与歌剧院舞台经理发生争执之后，古斯塔夫·马勒辞去了在莱比锡的职位。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (732, '1888年5月', '古斯塔夫·马勒', '莱比锡', 51.34063, 12.37473, '1888年5月，在与歌剧院舞台经理发生争执之后，古斯塔夫·马勒辞去了在莱比锡的职位。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (733, '1892年', '古斯塔夫·马勒', '汉堡', 53.54376, 10.00991, '1892年，古斯塔夫·马勒与汉堡的演唱者来到伦敦，参加为时六周的德国歌剧节。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (734, '1892年', '古斯塔夫·马勒', '伦敦', 51.50732, -0.12765, '1892年，古斯塔夫·马勒与汉堡的演唱者来到伦敦，参加为时六周的德国歌剧节。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (735, '1893年', '古斯塔夫·马勒', '奥地利', 47.20003, 13.19996, '1893年古斯塔夫·马勒在上奥地利州阿特湖畔施泰因巴赫的阿特湖边购下了一处夏居。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (736, '1895年12月13日', '古斯塔夫·马勒', '柏林', 52.51704, 13.38886, '古斯塔夫·马勒初次尝到作为作曲家的成功是在1895年12月13日，《第2号交响曲》在柏林首演，古斯塔夫·马勒亲自指挥。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (737, '1897年10月8日', '古斯塔夫·马勒', '维也纳', 48.20835, 16.37250, '1897年10月8日，古斯塔夫·马勒正式被任命为维也纳宫廷歌剧院的总监。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (738, '1897年8月', '古斯塔夫·马勒', '维也纳', 48.20835, 16.37250, '1897年8月，古斯塔夫·马勒赶回维也纳，来为《尼伯龙根的指环》在维也纳的首次完整演出做准备。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (739, '1899年', '古斯塔夫·马勒', '维也纳', 48.20835, 16.37250, '1899年古斯塔夫·马勒指挥了《第二交响曲》在维也纳的首演。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (740, '1901年', '古斯塔夫·马勒', '慕尼黑', 48.13711, 11.57538, '1901年古斯塔夫·马勒在慕尼黑首演了《第四交响曲》。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (741, '1902年', '古斯塔夫·马勒', '维也纳', 48.20835, 16.37250, '1902年，古斯塔夫·马勒遇到了维也纳分离派艺术家与设计师阿尔弗雷德·罗勒，并在1年后指定古斯塔夫·马勒为歌剧院的舞台设计师。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (742, '1904年', '古斯塔夫·马勒', '科隆', 50.93836, 6.95997, '1904年，古斯塔夫·马勒在科隆首演《第五交响曲》。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (743, '1905年1月', '古斯塔夫·马勒', '维也纳', 48.20835, 16.37250, '1905年1月，古斯塔夫·马勒在维也纳演出了四首《吕克特之歌》和《悼亡儿之歌》。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (744, '1907年', '古斯塔夫·马勒', '维也纳', 48.20835, 16.37250, '1907年，古斯塔夫·马勒疲于应付维也纳反对古斯塔夫·马勒的声浪，举家来到麦尔尼格。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (745, '1907年11月24日', '古斯塔夫·马勒', '维也纳', 48.20835, 16.37250, '1907年11月24日，古斯塔夫·马勒在维也纳进行了告别演出，曲目是古斯塔夫·马勒的《第二交响曲》。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (746, '1907年12月', '古斯塔夫·马勒', '维也纳', 48.20835, 16.37250, '1907年12月，古斯塔夫·马勒离开维也纳，渡海来到纽约。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (747, '1908年', '古斯塔夫·马勒', '奥地利', 47.20003, 13.19996, '1908年，古斯塔夫·马勒从纽约回到奥地利，并建立了第三个也是最后一个作曲工作室，位于奥地利博尔扎诺·南蒂罗尔自治省多比亚科附近的松树林中。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (748, '1908年1月1日', '古斯塔夫·马勒', '维也纳', 48.20835, 16.37250, '1908年1月1日，古斯塔夫·马勒第一次在大都会歌剧院亮相，指挥瓦格纳的《特里斯坦与伊索尔德》，用的是当时在纽约仍然奉为标准、在维也纳却早已过时的精简版。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (749, '1909年', '古斯塔夫·马勒', '意大利', 42.63843, 12.67430, '1909年的演出季大都会歌剧院让意大利指挥家阿图罗·托斯卡尼尼与古斯塔夫·马勒分担工作。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (750, '1909年', '古斯塔夫·马勒', '欧洲', 51.00000, 10.00000, '1909年夏天，古斯塔夫·马勒回到欧洲，投入到《第9号交响曲》的创作中，并去荷兰进行了巡演。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (751, '1909年12月16日', '古斯塔夫·马勒', '美国', 39.78373, -100.44588, '古斯塔夫·马勒的第一交响曲在1909年12月16日美国首演，反响很差。', '古斯塔夫·马勒');
INSERT INTO `musician` VALUES (752, '1910年9月12日', '古斯塔夫·马勒', '慕尼黑', 48.13711, 11.57538, '1910年9月12日，古斯塔夫·马勒的第8号交响曲在慕尼黑首演。', '古斯塔夫·马勒');
